DROP TABLE IF EXISTS `user_extends`;
CREATE TABLE `user_extends`  (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) DEFAULT NULL COMMENT '用户id',
  `real_name` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT '真实姓名',
  `sex` enum('0','1','2') CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT '0' COMMENT '性别(0保密,1男,2女)',
  `qq` varchar(11) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT 'QQ',
